const handleChange = (e) => {
  const { name, value } = e.target;
  let updatedValue = value;

  // Convert name and email to uppercase
  if (name === 'name' || name === 'email') {
    updatedValue = value.toUpperCase();
  }

  setFormData({
    ...formData,
    [name]: updatedValue
  });
};
