import React, { useState, useEffect } from 'react';

const NearestMunicipalityFinder = () => {
  const [userLocation, setUserLocation] = useState(null);
  const [nearestMunicipality, setNearestMunicipality] = useState('');
  const [minDistance, setMinDistance] = useState(null);

  useEffect(() => {
    const getUserLocation = () => {
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
          (position) => {
             { latitude, longitude } 

            reverseGeocode(latitude, longitude);
          },
          (error) => {
            console.error('Error getting user location:', error);
            handleLocationError(error);
          }
        );
      } else {
        console.error('Geolocation is not supported by this browser.');
        handleGeolocationNotSupported();
      }
    };

    const reverseGeocode = (latitude, longitude) => {
      // Perform reverse geocoding to retrieve human-readable location information
      // Example: Use a third-party geocoding service like Google Maps Geocoding API
      // Make an HTTP request to the geocoding API endpoint with the latitude and longitude
      // Parse the response to extract location details
      // Display the location information to the user or use it in your application
    };

    const handleLocationError = (error) => {
      // Handle the error gracefully, display a user-friendly message, and provide alternative options
    };

    const handleGeolocationNotSupported = () => {
      // Notify the user that geolocation is not supported by their browser
    };

    getUserLocation();
  }, []);

  useEffect(() => {
    const calculateDistance = (coord1, coord2) => {
      const R = 6371; // Radius of the Earth in kilometers
      const lat1 = coord1.latitude;
      const lon1 = coord1.longitude;
      const lat2 = coord2.latitude;
      const lon2 = coord2.longitude;

      const dLat = (lat2 - lat1) * Math.PI / 180;
      const dLon = (lon2 - lon1) * Math.PI / 180;
      const a =
        Math.sin(dLat / 2) * Math.sin(dLat / 2) +
        Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
        Math.sin(dLon / 2) * Math.sin(dLon / 2);
      const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
      const distance = R * c;

      return distance; // Distance in kilometers
    };

    const municipalities = [
      { name: 'Municipal council Bilaspur', latitude: 31.33918975292773, longitude: 76.75704773589915 },
      // Other municipality data here...
    ];

    if (userLocation) {
      let minDistance = Number.MAX_SAFE_INTEGER;
      let nearestMunicipality = '';

      municipalities.forEach(municipality => {
        const distance = calculateDistance(userLocation, {
          latitude: municipality.latitude,
          longitude: municipality.longitude
        });
        if (distance < minDistance) {
          minDistance = distance;
          nearestMunicipality = municipality.name;
        }
      });

      setNearestMunicipality(nearestMunicipality);
      setMinDistance(minDistance);
    }
  }, [userLocation]);

  return (
    <div>
      <h2>Nearest Municipality Finder</h2>
      {userLocation && (
        <p>Your current location: {userLocation.latitude}, {userLocation.longitude}</p>
      )}
      {nearestMunicipality && minDistance && (
        <p>The nearest municipality is {nearestMunicipality} at a distance of {minDistance.toFixed(2)} kilometers.</p>
      )}
    </div>
  );
};

export default NearestMunicipalityFinder;
