import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { ReactComponent as EyeIcon } from './eye.svg';
import { ReactComponent as EyeOffIcon } from './eye-off.svg';

function LoginPage({ setParentState, setActiveProfile }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState('');

  const updateParentState = () => {
    setParentState(true);
  };

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:5000/backlogin', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          email: email,
          password: password
        })
      });

      const data = await response.json();

      if (response.ok) {
        const userData = {
          name: data.user.name,
          email: data.user.email,
          phone: data.user.phone,
          profession: data.user.profession,
          password: data.user.password
        };

        setActiveProfile(userData);
        updateParentState();

        window.location.href = '/profile'; // Redirect to profile page upon successful login
      } else {
        setError(data.error || 'Login failed');
      }
    } catch (error) {
      console.error('Error during login:', error);
      setError('Internal server error');
    }
  };

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="max-w-md w-full bg-white p-8 rounded-lg shadow-md">
        <h2 className="text-2xl font-semibold text-center text-gray-800 mb-4">Login</h2>
        <form onSubmit={handleLogin}>
          <div className="mb-4">
            <label htmlFor="email" className="block text-gray-700 font-semibold mb-2">Email:</label>
            <input type="email" id="email" name="email" value={email} onChange={(e) => setEmail(e.target.value)} className="input-field" />
          </div>
          <div className="mb-4">
            <label htmlFor="password" className="block text-gray-700 font-semibold mb-2">Password:</label>
            <div className="relative">
              <input type={showPassword ? 'text' : 'password'} id="password" name="password" value={password} onChange={(e) => setPassword(e.target.value)} className="input-field" />
              <button type="button" onClick={togglePasswordVisibility} className="toggle-password">
                {showPassword ? <EyeOffIcon className="eye-icon" /> : <EyeIcon className="eye-icon" />}
              </button>
            </div>
          </div>
          {error && <p className="text-red-500 mb-4">{error}</p>}
          <button type="submit" className="btn-primary">Login</button>
        </form>
        <p className="mt-4 text-gray-600">
          Don't have an account? <Link to="/signup" className="text-blue-500 hover:text-blue-800 font-bold">Sign Up</Link>
        </p>
      </div>
    </div>
  );
}

export default LoginPage;
