import React, { useState } from 'react';
import axios from 'axios'; // Import axios for making HTTP requests

function UploadPage() {
  // Define state variables for image and description
  const [image, setImage] = useState(null);
  const [description, setDescription] = useState('');

  // Handle form submission
  const handleSubmit = async (event) => {
    event.preventDefault();

    // Create FormData object to append image and description
    const formData = new FormData();
    formData.append('image', image);
    formData.append('description', description);

    try {
      // Make POST request to backend
      const response = await axios.post('http://localhost:5000/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      // Handle success response
      console.log('File uploaded successfully', response.data);
    } catch (error) {
      // Handle error
      console.error('Error uploading file', error);
    }
  };

  // Handle file input change
  const handleFileChange = (event) => {
    setImage(event.target.files[0]);
  };

  // Handle description input change
  const handleDescriptionChange = (event) => {
    setDescription(event.target.value);
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center justify-center">
      <div className="max-w-md w-full bg-white p-8 rounded-lg shadow-md">
        <h2 className="text-2xl font-semibold text-gray-800 mb-4">Upload Image of Garbage</h2>
        {/* Image Upload Form */}
        <form className="space-y-4" onSubmit={handleSubmit}>
          {/* File Input */}
          <div>
            <label htmlFor="image" className="block text-gray-700 font-semibold">Select Image:</label>
            <input type="file" id="image" name="image" accept="image/*" className="mt-2 border-gray-300 focus:border-blue-500 focus:ring focus:ring-blue-500 focus:ring-opacity-50 rounded-md p-2 w-full" onChange={handleFileChange} />
          </div>
          {/* Description Input */}
          <div>
            <label htmlFor="description" className="block text-gray-700 font-semibold">Description:</label>
            <textarea id="description" name="description" rows="4" className="mt-2 border-gray-300 focus:border-blue-500 focus:ring focus:ring-blue-500 focus:ring-opacity-50 rounded-md p-2 w-full" value={description} onChange={handleDescriptionChange}></textarea>
          </div>
          {/* Submit Button */}
          <button type="submit" className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500">
            Upload
          </button>
        </form>
      </div>
    </div>
  );
}

export default UploadPage;
