import React, { useEffect, useState } from 'react';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
import Navbar from './components/Navbar';
import HomePage from './components/Homepage';
import AboutUsPage from './components/AboutUsPage';
import ResourcePage from './components/ResourcePage';
import ContactUs from './components/ContactUs';
import TaskQueue from './TaskQueue';
import LoginPage from './components/LoginPage';
import UploadPage from './components/UploadPage';
import ProfilePage from './components/Profile';
import SignUp from './components/SignUp';

export default function App() {
  // State to manage backend data and login status
  const [backendData, setBackendData] = useState({});
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    // Fetch backend data on component mount
    fetch("http://localhost:5000/api")
      .then(response => response.json())
      .then(data => setBackendData(data))
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  const updateLoginStatus = (status) => {
    setIsLoggedIn(status);
  };

  return (
    <>
      <BrowserRouter>
        <Navbar isLoggedIn={isLoggedIn} />
        <Routes>
          <Route exact path='/' element={<HomePage />} />
          <Route exact path='/upload' element={<UploadPage />} />
          <Route exact path='/task-queue' element={<TaskQueue />} />
          <Route exact path='/profile' element={<ProfilePage />} />
          <Route exact path='/signup' element={<SignUp />} />
          <Route exact path='/about' element={<AboutUsPage />} />
          <Route exact path='/resources' element={<ResourcePage />} />
          {/* Pass updateLoginStatus to LoginPage */}
          <Route
            exact
            path='/login'
            element={<LoginPage updateLoginStatus={updateLoginStatus} />}
          />
          <Route exact path='/contact' element={<ContactUs />} />
        </Routes>
      </BrowserRouter>
    </>
  );
}
