import React, { useState, useEffect } from 'react';

const NearestMunicipalityFinder = () => {
  const [userLocation, setUserLocation] = useState(null);
  const [nearestMunicipality, setNearestMunicipality] = useState('');
  const [minDistance, setMinDistance] = useState(null);

  useEffect(() => {
    // Function to get user's current location
    const getUserLocation = () => {
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition((position) => {
          setUserLocation({
            latitude: position.coords.latitude,
            longitude: position.coords.longitude
          });
        }, (error) => {
          console.error('Error getting user location:', error);
        });
      } else {
        console.error('Geolocation is not supported by this browser.');
      }
    };

    getUserLocation();
  }, []);

  useEffect(() => {
    // Function to calculate distance between two coordinates using Haversine formula
    const calculateDistance = (coord1, coord2) => {
      const R = 6371; // Radius of the Earth in kilometers
      const lat1 = coord1.latitude;
      const lon1 = coord1.longitude;
      const lat2 = coord2.latitude;
      const lon2 = coord2.longitude;

      const dLat = (lat2 - lat1) * Math.PI / 180;
      const dLon = (lon2 - lon1) * Math.PI / 180;
      const a =
        Math.sin(dLat / 2) * Math.sin(dLat / 2) +
        Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
        Math.sin(dLon / 2) * Math.sin(dLon / 2);
      const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
      const distance = R * c;

      return distance; // Distance in kilometers
    };

    // List of municipalities with their coordinates
    const municipalities = [
      { name: 'Municipality A', latitude: 123.456, longitude: 456.789 },
      { name: 'Municipality B', latitude: 111.222, longitude: 333.444 },
      // Add more municipalities as needed
    ];

    // Find the nearest municipality
    if (userLocation) {
      let minDistance = Number.MAX_SAFE_INTEGER;
      let nearestMunicipality = '';

      municipalities.forEach(municipality => {
        const distance = calculateDistance(userLocation, {
          latitude: municipality.latitude,
          longitude: municipality.longitude
        });
        if (distance < minDistance) {
          minDistance = distance;
          nearestMunicipality = municipality.name;
        }
      });

      // Update state with nearest municipality and distance
      setNearestMunicipality(nearestMunicipality);
      setMinDistance(minDistance);
    }
  }, [userLocation]);

  return (
    <div>
      <h2>Nearest Municipality Finder</h2>
      {userLocation && (
        <p>Your current location: {userLocation.latitude}, {userLocation.longitude}</p>
      )}
      {nearestMunicipality && minDistance && (
        <p>The nearest municipality is {nearestMunicipality} at a distance of {minDistance.toFixed(2)} kilometers.</p>
      )}
    </div>
  );
};

export default NearestMunicipalityFinder;
