import React, { useState, useEffect } from 'react';

function ProfilePage({ user }) {
  // State variables for handling requests and errors
  const [editLoading, setEditLoading] = useState(false);
  const [deleteLoading, setDeleteLoading] = useState(false);
  const [error, setError] = useState(null);

  // Function to handle editing the user profile
  const handleEditProfile = async () => {
    setEditLoading(true);
    try {
      // Send POST request to /edit endpoint
      const response = await fetch('/edit', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ /* Include data to edit */ }),
      });
      // Handle response if needed
    } catch (error) {
      setError(error.message);
    } finally {
      setEditLoading(false);
    }
  };

  // Function to handle deleting the user account
  const handleDeleteAccount = async () => {
    setDeleteLoading(true);
    try {
      // Send POST request to /delete endpoint
      const response = await fetch('/delete', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email: user.email }), // Assuming email is the unique identifier
      });
      // Handle response if needed
    } catch (error) {
      setError(error.message);
    } finally {
      setDeleteLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center justify-center">
      <div className="max-w-md w-full bg-white p-8 rounded-lg shadow-md">
        <h2 className="text-2xl font-semibold text-gray-800 mb-4">Profile</h2>
        {/* Profile information */}

        <div className="flex justify-between">
          <button 
            onClick={handleEditProfile} 
            className={`bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 ${editLoading ? 'opacity-50 cursor-not-allowed' : ''}`}
            disabled={editLoading}
          >
            {editLoading ? 'Editing...' : 'Edit Profile'}
          </button>
          <button 
            onClick={handleDeleteAccount} 
            className={`bg-red-500 hover:bg-red-600 text-white font-semibold py-2 px-4 rounded-md focus:outline-none focus:ring-2 focus:ring-red-500 ${deleteLoading ? 'opacity-50 cursor-not-allowed' : ''}`}
            disabled={deleteLoading}
          >
            {deleteLoading ? 'Deleting...' : 'Delete Account'}
          </button>
        </div>
      </div>
    </div>
  );
}

export default ProfilePage;
