import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

function LoginPage() {
  const [usernameOrEmail, setUsernameOrEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:5000/backlogin', {
        email: usernameOrEmail,
        password: password
      });

      // Handle successful login
      console.log(response.data.message);
      // You can handle setting isLoggedIn state in your component accordingly

    } catch (error) {
      if (error.response && error.response.data) {
        // Handle login error
        setError(error.response.data.error);
      } else {
        setError('Internal server error');
      }
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      {/* Header Section */}
      <header className="mb-8">
        <h1 className="text-2xl font-bold text-center text-gray-800">Your App Name</h1>
      </header>

      {/* Login Form */}
      <form className="w-full max-w-sm bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="usernameOrEmail">
            Username or Email
          </label>
          <input
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            id="usernameOrEmail"
            type="text"
            placeholder="Enter your username or email"
            value={usernameOrEmail}
            onChange={(e) => setUsernameOrEmail(e.target.value)}
          />
        </div>
        <div className="mb-6">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="password">
            Password
          </label>
          <input
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 mb-3 leading-tight focus:outline-none focus:shadow-outline"
            id="password"
            type="password"
            placeholder="Enter your password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <p className="text-red-500 text-xs italic">Please enter your password.</p>
        </div>
        <div className="flex items-center justify-between">
          <button
            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
            type="button"
            onClick={handleLogin}
          >
            Sign In
          </button>
          <a className="inline-block align-baseline font-bold text-sm text-blue-500 hover:text-blue-800" href="#">
            Forgot Password?
          </a>
        </div>
      </form>

      {/* Social Login Integration */}
      <div className="flex justify-center items-center mb-4">
        {/* Example social login buttons (replace with actual icons) */}
        <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded mr-2 focus:outline-none focus:shadow-outline">
          Sign in with Facebook
        </button>
        <button className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">
          Sign in with Google
        </button>
      </div>

      {/* Signup Option */}
      <p className="text-sm text-gray-600 mb-4">
        Don't have an account? &nbsp;
        <Link to="/signup" className="text-blue-500 hover:text-blue-800 font-bold">Sign Up</Link>
      </p>

      {/* Error Message */}
      {error && <p className="text-red-500">{error}</p>}

      {/* Footer Section */}
      <footer>
        <p className="text-xs text-gray-500">&copy; 2024 Your App Name. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default LoginPage;
